mapper.update:
update sb_route_station
set Valid = #{valid}
where STATION_ID = #{stationId}

update sb_route_station
set Valid = #{valid}
where ROUTE_ID = #{routeId}
  and STATION_ID = #{stationId}

update sb_route_station
set SBS_DEPART_TIME = #{sbsDepartTime}
where Valid = 1
  and ROUTE_ID = #{routeId}
  and STATION_ID = #{stationId}
